--
-- Cấu trúc bảng cho bảng `stories`
--

CREATE TABLE `stories` (
  `id` int(11) NOT NULL,
  `name` varchar(255) NOT NULL COMMENT 'Tên truyện',
  `recap` text NOT NULL COMMENT 'Tóm tắt',
  `category_id` int(11) NOT NULL COMMENT 'Tên danh muc',
  `image` varchar(100) NOT NULL COMMENT 'hình ảnh',
  `created` datetime NOT NULL,
  `modified` datetime NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `stories`
--

INSERT INTO `stories` (`id`, `name`, `recap`, `category_id`, `image`, `created`, `modified`) VALUES
(1, 'THIÊN ĐƯỜNG CẨM tú', 'Xuyên qua là một kiện rất có ý tứ sự tình, nhưng khi Phòng Tuấn xuyên qua đến vị kia toàn thân bốc lên xanh mơn mởn quang mang Đường triều cùng tên tiền bối trên người, cũng cảm giác sinh hoạt tất cả đều không tốt.', 3, 'story-img/thien-duong-cam-tu.jpg', '2021-06-29 09:34:32', '2021-07-02 06:03:05'),
(2, 'GIANG SƠN TRANH ANH HÙNG', 'Giang sơn như họa, chi linh phá nát, kim qua thiết mã, khói lửa kiêu hùng!\r\nĐây là một cái phồn hoa như gấm Đại Tùy thịnh thế, này lại là toàn bộ khói lửa cao chót vót thời đại.\r\nTài tử giai nhân, Hồng Tụ du hiệp, thi từ ca phú, kiếm đảm cầm tâm.\r\nDương Tố, Cao Dĩnh, Bùi Củ, Ngu Thế Cơ, Vũ Văn Thuật, Lý Mục, triều đình ám đấu, môn phiệt vòng xoáy...\r\nLý Mật, Đậu Kiến Đức, Đỗ Phục Uy, Vương Thế Sung, Lưu Vũ Chu, Lương Sư Đô, Tiêu Tiển, hào hùng san sát...\r\nTùy thất thiên hạ, đại tranh thời loạn lạc, ai có thể bình định càn khôn, chấp chưởng xã tắc?\r\nBạch mã ngân thương La Chiêu Vân, mang ngươi đi vào cái kia ầm ầm sóng dậy Tùy mạt thời đại!', 3, 'story-img/giang-son-tranh-hung.jpg', '2021-07-01 03:07:40', '2021-07-01 03:08:04'),
(3, 'LỢI HẠI TA NGƯỜI NGUYÊN THUỶ', 'Bạn đang theo dõi truyện mới full Lợi Hại Ta Người Nguyên Thủy của tác giả Trúc Thứ Vô Phong rất hấp dẫn và lôi cuốn. Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới mới lạ, những tình tiết đặc sắc, đọc truyện Huyền Huyễn, Trọng Sinh này để trải nghiệm và cảm nhận bạn nhé.\r\nLệ Hại Liễu Ngã Đích Nguyên Thủy Nhân (Lợi hại thật người nguyên thủy của ta)\r\nTrọng sinh đến xã hội nguyên thuỷ, ý không ngoài ý muốn, kinh không kinh hỉ?\r\nDiệp Hi trọng sinh, hắn trong lòng lại có một vạn đầu thảo nê mã ở lao nhanh.\r\nThạch thán kỷ cự trùng, Jurassic khủng long, cổ gần kỷ mãnh thú, đây là cái nào thời không nguyên thủy thời đại?\r\nNhân loại như vậy yếu đuối? Nói tốt trọng sinh phúc lợi đâu?\r\nKhông có biện pháp, Diệp Hi chỉ có thể mang theo người nguyên thủy, thổi lên quật khởi nguyên thủy thế giới kèn!\r\nNgười nguyên thủy: Ngao ngao ngao…… Lợi hại ta ca!', 1, 'story-img/loi-hai-ta-nguoi-nguyen-thuy.jpg', '2021-07-01 03:11:24', '2021-07-01 03:11:24'),
(4, 'VŨ HIỆP KIÊU HÙNG', 'Tiên nhân phủ ta đỉnh, thân thụ Bạch Ngọc Kinh .\r\nTô Lưu kéo Hồng Tụ, xách một bầu rượu .\r\nUống tràn tung ca, tận tình cẩu thả .\r\nMột ngựa bạch mã đi vạn giới giang hồ, trong nháy mắt cũng gọi là Thiên Khuynh .\r\nTiểu Lý Thám Hoa cái kia ngồi yên một đao, đã hiểu mới biết tịch mịch vô địch\r\nTrong nội tâm của ta vẫn còn có nhất kiếm, kiếm hai mươi ba\r\nCó thể trảm Lục Địa Thần Tiên.', 1, 'story-img/vu-hiep-kieu-hung.jpg', '2021-07-01 03:12:28', '2021-07-01 03:12:28'),
(5, 'ANH QUÂN LINH VƯƠNG', 'Bạn đang theo dõi dđọc truyện online Anh Linh Quân Vương của tác giả Cát Thời Gian rất hấp dẫn và lôi cuốn. Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới mới lạ, những tình tiết đặc sắc, đọc truyện Võng Du, Khoa Huyễn, Trọng Sinh này để trải nghiệm và cảm nhận bạn nhé.\r\nTỉnh lại sau giấc ngủ phát hiện mình đã trở lại hai năm trước, cái kia khoản được xưng dung hợp anh hùng vô địch cùng ma thú tranh bá nguyên tố vương quốc Vĩnh Hằng vừa rồi bắt đầu open beta, đời này Lâm Hàn không lại cam tâm làm một cái không có tiếng tăm gì tiểu lãnh chúa, hắn âm thầm xin thề, hắn muốn dẫn chính mình bộ đội chinh chiến thiên hạ, đi tranh thủ một đời trước không từng có qua đồ vật.\r\nCấp độ truyền thuyết anh hùng ở trên trời đối chiến, minh giới thánh nữ ở trên chiến trường than nhẹ, tử vong sóng gợn mang đến minh giới tuyệt vọng, trong bóng tối, Tử Vong kỵ sĩ chính đang không cam lòng gào thét, Cốt Long cùng Băng Sương Cự Long chiếm cứ bầu trời, phưởng Chức Nữ kết trên tử vong mạng nhện chờ đợi con mồi giáng lâm, căm hận vung lên dao phay cùng quỷ ăn xác đồng thời đến, mũi kiếm chỉ, Vong Linh đại quân lần thứ hai đột kích, chấn động hết thảy thế nhân.\r\nMà Lâm Hàn, sẽ mang dẫn chúng nó, bao phủ tới, chinh chiến thiên hạ, bình định tất cả có can đảm chặn ở trước mặt hắn kẻ địch, cuối cùng đăng cơ làm vua!', 1, 'story-img/anh-linh-quan-vuong.jpg', '2021-07-01 03:13:13', '2021-07-01 03:13:13'),
(6, 'KIẾM NGẠO TRÙNG SINH', 'Bạn đang theo dõi truyen dai hay full Kiếm Ngạo Trùng Sinh của tác giả Mông Bạch rất hấp dẫn và lôi cuốn. Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới mới lạ, những tình tiết đặc sắc, đọc truyện Kiếm Hiệp, Huyền Huyễn, Trọng Sinh này để trải nghiệm và cảm nhận bạn nhé.\r\nMột thế hệ Kiếm Thần Phong Tử Nhạc lấy kiếm nhập đạo, đột phá tiên thiên cảnh giới.\r\nNhất kiếm tây lai, thiên hạ cúi đầu, nhưng hắn cả đời bên trong gặp gỡ bi thảm, thân nhân ly tán, tuy rằng vô địch, nhưng cũng có khôn cùng tiếc nuối.\r\nNgẫu nhiên cơ hội, hắn chiếm được một bộ có thể nhất khuy rất cao võ học cảnh giới bí kíp [ Thái Huyền cảm ứng thiên ], gặp được cao thủ vây công, ngọc thạch câu phần. Ở hắn tỉnh lại thời điểm, lại phát hiện chính mình trọng sinh về tới ba mươi năm trước thiếu niên khi.\r\nHết thảy tiếc nuối, đều có thể bù lại; Mà kiếm đạo tối cao cảnh giới, cũng có thể đủ lại lần nữa đột phá!nhất đại kiếm thần phong tử nhạc dĩ kiếm nhập đạo，đột phá tiên thiên cảnh giới。nhất kiếm tây lai，thiên hạ phủ，đãn tha nhất sanh chi trung tế ngộ bi thảm，thân nhân ly tán，tuy nhiên vô địch，khước dã hữu trứ vô biên đích di hám。ngẫu nhiên ky hội，tha đắc đáo liễu nhất bộ năng cú nhất khuy canh cao vũ học cảnh giới đích bí cấp《thái huyền cảm ứng thiên》，tao ngộ cao thủ vi công，ngọc thạch câu phần。tại tha tỉnh lai đích thì hậu，khước hiện tự kỷ trọng sanh hồi đáo liễu tam thập niên tiền đích thiểu niên thì。nhất thiết đích di hám，đô khả dĩ di bổ；nhi kiếm đạo đích chí cao cảnh giới，dã năng cú tái độ đột phá!', 1, 'story-img/kiem-ngao-trung-sinh.jpg', '2021-07-01 03:13:59', '2021-07-01 03:13:59'),
(7, 'QUỐC GIA ÁC QUỶ', 'Một ngày bắt đầu từ ánh sáng chói lọi kết thúc bằng một màn đêm u tối. Khi đó mọi người sẽ chìm vào giấc ngủ mộng mị.\r\nCon người thường chọn cách giấu đi chân tướng sự thật ở một góc tối lạnh lẽo. Có người thấy, nhưng lựa chọn bỏ qua.\r\nCon mèo với con ngươi đỏ thẫm, trong đêm tối đứng gọi đàn.\r\nTrong bóng tối... có bóng người quái dị đang không ngừng tiến đến!\r\nChó cùng rứt dậu, ác quỷ tụ hội, tuyệt vọng và căng thẳng đến cực điểm!\r\nVô số những sự kiện khiến người ta phải sởn gai ốc liên tục xuất hiện. Là trầm luân trong tuyệt vọng? Hay là trong tuyệt vọng tìm được lối thoát?\r\nVẫn phải chờ đợi lời tuyên án tàn nhẫn đến từ số phận?\r\nĐây là thế giới tại quốc gia ác quỷ, một thế giới tràn đầy khủng hoảng và chém giết.\r\nHạ Thiên Kỳ đến đăng ký xin việc cho một công ty, chỉ cần trong thời gian thử việc hắn làm tốt nhiệm vụ thì có thể được lương tháng 2 vạn, một chiếc xe Audi, cũng như được tặng lấy biệt thự khi làm cho 1 công ty thần bý\r\nVốn tưởng rằng gia nhập công ty này, sẽ giúp cuộc đời của hắn sẽ đổi vận, nhưng trớ trêu thay hắn mau chóng phát hiện, chân tướng không hề tốt như hắn nghĩ.\r\nMột đống chuyện quỷ dị ly kỳ liên tiếp phát sinh, những bí ẩn lần lượt trồi lên mặt nước.\r\nNgay lúc Hạ Thiên Kỳ phát giác, thì hắn đã không còn đường để quay đầu lại.\r\nSống hay chết? Ở kia xa xôi không biết, hắn không có lựa chọn nào khác.', 2, 'story-img/quoc-gia-ac-quy.jpg', '2021-07-01 03:15:35', '2021-07-01 03:15:53'),
(8, 'TUYỆT SẮC BẢN LÃO NƯƠNG', 'Ngô Hổ Thần sống lại sống đến mới vừa lên trung học đệ nhị cấp năm ấy nghỉ hè, đời trước hắn không có trở thành một nam nhân thành công, mà lại mất đi hắn yêu nhất Tiểu Di, đời này, hắn quyết định lại bắt đầu lại từ đầu, đem lấy hết tất cả hot girl, thanh thuần học sinh nữ, tài trí nữ lão sư, phong tình tiểu thiếu phụ, anh khí nữ hoa khôi cảnh sát, điêu ngoa tiểu thái muội, những nữ nhân này, đặc biệt hắn Tiểu Di, hắn nhất định phải hết thảy thu được hậu cung. \' cao giàu đẹp là cái gì? Mà lại xem một cái tia nam sống lại đại nghịch chuyển.', 2, 'story-img/tuyet-sac-lao-ban-nuong.jpg', '2021-07-01 03:16:57', '2021-07-01 03:16:57'),
(9, 'SWORD ART ONLINE', 'Bạn đang theo dõi truyen moi full Sword Art Online của tác giả Kawahara Reki rất hấp dẫn và lôi cuốn.Là một truyện được giới thiệu với bạn đọc trên trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt vào một thế giới mới lạ, những tình tiết đặc sắc, đọc truyện Võng Du, Khoa Huyễn, Huyền Huyễn, Dị Giới, Dị Năng, Sắc, Truyện Teen, Light Novel này để trải nghiệm và cảm nhận bạn nhé.Một trò chơi nhưng lại liên quan chặt chẽ với hiện thực. Đã tham gia vào thì chỉ có một con đường thoát chết đó chính là phá đảo game; nếu gameover thì sẽ đồng nghĩa với cái chết. 10000 người không hề biết sự thực được ẩn giấu này đã đăng nhập vào \"Sword Art Online\", một game thế hệ mới của thể loại game MMO.Một mình tham gia trò chơi, Kirito đã phát hiện ra bí mật này, cậu phải dũng cảm đối mặt chiến đấu với những điều đáng sợ. Mục tiêu của cậu là phá đảo game thoát ra ngoài.Asuna, một người dùng kiếm điêu luyện đã đưa ra một lời mời hấp dẫn đối với cậu. Cậu ta quyết định chiến đấu cùng cô ấy, chính cuộc gặp gỡ này đã làm thay đổi định mệnh của Kirito.', 1, 'story-img/sword-art-online.jpg', '2021-07-01 03:17:59', '2021-07-01 06:01:01'),
(10, 'MAOU GAKUIN NO FUTEKIGOUSHA', 'Sau một thời gian dài đối địch với con người, tinh linh và cả những vị thần, trải qua vô vàn trận chiến và xung đột, Quỷ vương Arnos đã trở nên chán ngấy và mệt mỏi với tất cả những điều đó, và bắt đầu mong mỏi một thế giới hòa bình. Vì vậy, gã quỷ vương ấy đã quyết định hi sinh thân mình để xây dựng bức tường phân chia thế giới, chấm dứt cuộc chiến tranh, và chuyển sinh đến 2000 năm sau.\r\nTuy nhiên, những gì chờ đợi cậu ta sau khi tái sinh là một thế giới vì quá quen với sự bình an mà con cháu của cậu trở nên quá yếu ớt , ma thuật sụt giảm nghiêm trọng, và những đại ma pháp thì hầu như biến mất.\r\nArnos theo học tại Học viện Quỷ vương - nơi được thành lập để tìm bất kỳ học viên nào có thể là tái sinh của quỷ vương, nhưng khả năng của cậu ta quá phi thường đến mức những người trong học viện không thể đánh giá đúng khả năng của cậu ta, vì vậy Arnos bị mọi người gắn cho cái mác là “Kẻ không phù hợp”. Bị đánh giá thấp và xa lánh bởi hầu hết mọi người ở đó, nhưng rồi cậu cũng đã gặp được một cô gái có thái độ thân thiện với mình, Misha, là thuộc hạ, cũng như đồng chí đầu tiên của cậu trong thời đại này,. Cả hai cùng chung một mục tiêu, phấn đấu leo lên đỉnh của hệ thống phân cấp quỷ để một ngày không xa đòi lại danh hiệu và địa vị đã từng là của cậu.', 1, 'story-img/maou-gakuin-no-futekigousha.jpg', '2021-07-01 03:20:17', '2021-07-01 03:20:17');

